    private void Botonigual1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Botonigual1ActionPerformed
        // TODO add your handling code here:
        double res = 0;
        int resint = (int) res;
        int res_b = 0;

        //equals se usa para comparar dos string
        if (op.equals("+")) {
            res = memoria + Double.parseDouble(pantalla.getText());

            resint = (int) res;
        } else if (op.equals("-")) {
            res = memoria - Double.parseDouble(pantalla.getText());

            resint = (int) res;
        } else if (op.equals("*")) {
            res = memoria * Double.parseDouble(pantalla.getText());
            resint = (int) res;
        } else if (op.equals("/")) {
            res = memoria / Double.parseDouble(pantalla.getText());
            resint = (int) res;
        } else if (op.equals("sqrt")) {
            res = Math.sqrt(Double.parseDouble(pantalla.getText()));
            resint = (int) res;
        } else if (op.equals("1/x")) {
            res = 1 / (Double.parseDouble(pantalla.getText()));
            resint = (int) res;
        } else if (op.equals("x^3")) {
            res = Math.pow(Double.parseDouble(pantalla.getText()), 3);
            resint = (int) res;
        } else if (op.equals("x^2")) {
            res = Math.pow(Double.parseDouble(pantalla.getText()), 2);
            resint = (int) res;
        } else if (op.equals("%")) {
            res = (memoria / 100) * Double.parseDouble(pantalla.getText());

            resint = (int) res;
        }

        //se usa para sacar la parte entera del numero en double
        long iPart = (long) res;
        double restpart = res - iPart;
        if (restpart != 0) {
            pantalla.setText(String.valueOf(res));
        } else {
            pantalla.setText(String.valueOf(resint));
        }


    }//GEN-LAST:event_Botonigual1ActionPerformed

