    private void BotonbinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonbinActionPerformed
        // TODO add your handling code here:
        String binario = "";
        double res = 0;
        op = "bin";
        pantalla.setText(m);
        if (op.equals("bin")) {
            res = Double.parseDouble(pantalla.getText());
            if (res > 0) {
                while (res > 0) {
                    if (res % 2 == 0) {
                        binario = "0" + binario;
                    } else {
                        binario = "1" + binario;
                    }
                    res = (int) res / 2;
                }
            } else if (res == 0) {
                binario = "0";

            } else {
                binario = "No se pudo convertir el numero. Ingrese solo numeros positivos";
            }
            pantalla.setText(binario);
        }
    }//GEN-LAST:event_BotonbinActionPerformed

