//Metodo para verificar que si hay un punto en la pantalla
    public static boolean existe_punto(String cadena) {
        boolean resultado;
        resultado = false;

        for (int i = 0; i < cadena.length(); i++) {
            if (cadena.substring(i, i + 1).equals(".")) {
                resultado = true;
                break;

            }

        }
        return resultado;

    }

    public static int binaryToDecimal(int number) {
        int decimal = 0;
        int binary = number;
        int power = 0;
        
        while (binary != 0) {

            int lastDigit = binary % 10;

            decimal += lastDigit * Math.pow(2, power);

            power++;

            binary = binary / 10;

        }

        return decimal;

    }


